import { Goal } from "../../../types";

export function showGoal(goal: Goal): string {
  const showKind = goal.kind == 'prove' ? "To prove" 
                 : goal.kind == 'disprove' ? "To disprove" 
                 : "Make undecidable"
  const showGoal = goal.expr.join("")
  return showKind + ": [" + showGoal + "]"
}
