import { useDrop } from "react-dnd"
import { ItemTypes } from "./ItemTypes"
import React, { useContext } from "react"
import Context from "./gameContext"
import { Card } from "react-bootstrap"

interface PremiseDropSlotProps {
  premiseIndex: number
  slotPosition: number
  isLocked: boolean
}

export const PremiseClickSlot = ({
  premiseIndex,
  slotPosition,
  isLocked,
}: PremiseDropSlotProps) => {

  const context = useContext(Context)

  return (
    <div style={
      {
        width: '1rem',
        height: '80%',
        marginTop: '5px',
        marginBottom: '5px',
        borderRadius: '20%',
        backgroundColor: isLocked ? "white" : "purple"
      }}

      onClick={() => {
        if (!isLocked && context.handCardSelected) {
          context.moveHandCardToPremise(premiseIndex, slotPosition)
        }
      }}
    >

    </div>
  )

}
