import React from 'react'
import { Goal } from '../../types'
import { Card } from 'react-bootstrap'
import { showGoal } from '../../server/gameApp/game/goal'

interface GoalsProps {
  goals: Goal[],
  isSuccessful: boolean[]
}

export const GoalsComponent = ({
  goals,
  isSuccessful
}: GoalsProps) => {

  const renderGoal = (i: number) => {
    return <Card>
      <Card.Text>{goals.map(it => showGoal(it))}</Card.Text>
      <Card.Text>{isSuccessful.toString()}</Card.Text>
    </Card>
  }

  const renderGoals = () => {
    const result: JSX.Element[] = []
    for(let i = 0; i < goals.length; i++) {
      result.push(renderGoal(i))
    }
    return result
  }

return <div>{
    renderGoals()
  }</div>

}
