import React from 'react'
import * as Types from '../../types'
import './Card.css'
import Card from 'react-bootstrap/Card'
import A_Card from '../../card_UI/A.jpg'
import B_Card from '../../card_UI/B.jpg'
import C_Card from '../../card_UI/C.jpg'
import D_Card from '../../card_UI/D.jpg'
import AND_Card from '../../card_UI/AND.jpg'
import OR_Card from '../../card_UI/OR.jpg'
import NOT_Card from '../../card_UI/NOT.jpg'
import LP_Card from '../../card_UI/LP.jpg'
import RP_Card from '../../card_UI/RP.jpg'
import IMPLY_Card from '../../card_UI/IMPLY.jpg'
import IFF_Card from '../../card_UI/IFF.jpg'

interface CardProps {
  cardSymbol: Types.Card
  colour: string
  onClick: () => void
}

export const Exit = (): JSX.Element => {
  return (
    <div>heading</div>
  )
}

export const CardComponent = ({
  cardSymbol,
  colour,
  onClick = (() => {})
}: CardProps): JSX.Element => {
  switch (cardSymbol) {
    case 'A': { colour = A_Card; break;}
    case 'B': { colour = B_Card; break;}
    case 'C': { colour = C_Card; break;}
    case 'D': { colour = D_Card; break;}
    case '&': { colour = AND_Card; break;}
    case '|': { colour = OR_Card; break;}
    case '~': { colour = NOT_Card; break;}
    case '(': { colour = LP_Card; break;}
    case ')': { colour = RP_Card; break;}
    case '->': { colour = IMPLY_Card; break;}
    case '==': { colour = IFF_Card; break;}
  }
  // if (cardSymbol == 'B') {
  //   colour = B_Card
  // }
  return (
    <Card style={{ width: '60px', height: '100px', backgroundImage: "url(" + colour + ")", backgroundSize: '60px'}} onClick={onClick}/>
    // <Card style={{ width: '4rem', padding: '5px', margin: '5px'}} onClick={onClick}>
  )
}
