import React, { useState, useEffect } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import {BrowserRouter as Router, Switch, Route, Redirect} from 'react-router-dom';
import { LevelBoardComponent } from './components/game/LevelBoard';
import { Entry } from './components/entry/Entry';
import { LevelPage } from './components/entry/levelPage';
import { UserCredential } from './types';
import { LoginContext } from './LoginContext';
import { LevelEditor } from './components/editor/LevelEditor';


function App() {

  const [user, setUser] = useState<UserCredential | undefined>(undefined)

  return (
    <div className="App">
      <LoginContext.Provider value={{ user: user, toggleSetUser: setUser }}>
        <Route exact path="/" component={Entry}></Route>
        <Route path="/levelPage/:sid" component={LevelPage}></Route>
        <Route path="/level/:sid/:levelID" component={LevelBoardComponent}></Route>
        <Route path="/editor" component={LevelEditor}></Route>
      </LoginContext.Provider>
    </div>
  );
}

export default App;
