import React, { useContext, useState } from "react";
import "./Entry.css";
import "bootstrap/dist/css/bootstrap.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit, faPlay } from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";
import { Avatar, IconButton, makeStyles } from "@material-ui/core";
import { LoginDialogue } from "../auth/Login";
import { RegisterDialogue } from "../auth/Register";
import { LoginContext } from "../../LoginContext";

type DialogState = "hide" | "login" | "signup";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexWrap: "wrap",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: "25ch",
  },
}));

export const Entry = (): JSX.Element => {
  const classes = useStyles();
  const logins = useContext(LoginContext);
  const [show, setShow] = useState<DialogState>("hide");
  const [refresher, setRefresher] = useState(true);
  const refresh = () => setRefresher(!refresher);

  const handleClose = () => {
    refresh();
    setShow("hide");
  };

  return (
    <div className={"entry"}>
      <div className={"login"}>
        {/* <LinkContainer to="/"> */}
        <IconButton onClick={() => setShow("login")}>
          <Avatar>{logins.user?.username.slice(0, 2)}</Avatar>
          {logins.user?.username ?? "Login"}
        </IconButton>
        {/* </LinkContainer> */}
        {/* Sign-up Dialogue */}
        <RegisterDialogue
          open={show === "signup"}
          onClose={handleClose}
          onSwitchToLogin={() => setShow("login")}
        />
        {/* Login Dialogue */}
        <LoginDialogue
          open={show === "login"}
          onClose={handleClose}
          onSwitchToSignup={() => setShow("signup")}
        />
      </div>
      <div className={"game_name"}>ParaDox</div>
      <div className={"fst_choice"}>
        <Link to="/levelPage/0" className={"link"}>
          <FontAwesomeIcon icon={faPlay} style={{ color: "white" }} />
          &ensp;Start
        </Link>
      </div>
      <div className={"snd_choice"}>
        <Link to="/editor" className={"link"}>
          <FontAwesomeIcon icon={faEdit} style={{ color: "white" }} />
          &ensp;Editor
        </Link>
      </div>
    </div>
  );
};
